CREATE VIEW vw_usuario_ingreso AS
 select `u`.`IdUsuario` AS `IdUsuario`,`u`.`NombreUsuario` AS `NombreUsuario`,`u`.`ApellidoUsuario` AS `ApellidoUsuario`,`u`.`DocumentoUsuario` AS `DocumentoUsuario`,`u`.`TelefonoUsuario` AS `TelefonoUsuario`,`u`.`CelularUsuario` AS `CelularUsuario`,`u`.`CorreoUsuario` AS `CorreoUsuario`,`u`.`EstadoUsuario` AS `EstadoUsuario`,`ia`.`IdIngresoAplicacion` AS `IdIngresoAplicacion`,`ia`.`Fk_Id_Usuario` AS `Fk_Id_Usuario`,`ia`.`Clave` AS `Clave`,`ia`.`PreguntaSeguridad` AS `PreguntaSeguridad`,`ia`.`RespuestaSeguridad` AS `RespuestaSeguridad`,`ia`.`UltimaActividad` AS `UltimaActividad`,`ia`.`EstadoIngreso` AS `EstadoIngreso`,`e`.`Cargo` AS `Cargo` from ((`usuario` `u` join `ingreso_aplicacion` `ia` on((`u`.`IdUsuario` = `ia`.`Fk_Id_Usuario`))) join `empleado` `e` on((`u`.`IdUsuario` = `e`.`Fk_Id_Usuario_Empleado`)))