CREATE VIEW vw_productos_proveedor AS select `p`.`IdProducto` AS `IdProducto`,`p`.`CodigoProducto` AS `CodigoProducto`,`p`.`NombreProducto` AS `NombreProducto`,`p`.`DescripcionProducto` AS `DescripcionProducto`,`p`.`Fk_Id_Categoria` AS `Fk_Id_Categoria`,`p`.`PrecioVentaDefinitivo` AS `PrecioVentaDefinitivo`,`p`.`EstadoProducto` AS `EstadoProducto`,`p`.`ExistenciasTotalBodega` AS `ExistenciasTotalBodega`,`p`.`ExistenciasTotalTienda` AS `ExistenciasTotalTienda`,`c`.`IdCategoriaProducto` AS `IdCategoriaProducto`,`c`.`NombreCategoriaProducto` AS `NombreCategoriaProducto`,`c`.`DescripcionCategoriaProducto` AS `DescripcionCategoriaProducto`,`c`.`EstadoCategoriaProducto` AS `EstadoCategoriaProducto`,`dpp`.`IdDetalleProveedor` AS `IdDetalleProveedor`,`dpp`.`Fk_Id_Producto` AS `Fk_Id_Producto`,`dpp`.`Fk_Id_Proveedor` AS `Fk_Id_Proveedor`,`dpp`.`EstadoDetalleProductoProveedor` AS `EstadoDetalleProductoProveedor`,`dpp`.`PrecioSugeridoVenta` AS `PrecioSugeridoVenta`,`dpp`.`ExistenciasBodega` AS `ExistenciasBodega`,`dpp`.`ExistenciasDevolucion` AS `ExistenciasDevolucion`,`pr`.`IdProveedor` AS `IdProveedor`,`pr`.`NombreProveedor` AS `NombreProveedor`,`pr`.`NombreContactoProveedor` AS `NombreContactoProveedor`,`pr`.`TelefonoContactoProveedor` AS `TelefonoContactoProveedor`,`pr`.`CorreoContactoProveedor` AS `CorreoContactoProveedor`,`pr`.`EstadoProveedor` AS `EstadoProveedor` from (((`producto` `p` join `categoria_producto` `c` on((`p`.`Fk_Id_Categoria` = `c`.`IdCategoriaProducto`))) join `detalle_producto_proveedor` `dpp` on((`dpp`.`Fk_Id_Producto` = `p`.`IdProducto`))) join `proveedor` `pr` on((`pr`.`IdProveedor` = `dpp`.`Fk_Id_Proveedor`)))